import React, { useState, useEffect } from 'react';
import Expenses from './components/Expenses';
import NewExpense from './components/NewExpense';
import ExpensesFilter from './components/ExpensesFilter';
import Chart from './components/Chart';
import { getExpenses } from './services/expenses'; // Підключіть сервіс для отримання витрат
import './App.css';

const App = () => {
  const [expenses, setExpenses] = useState([]);
  const [filteredYear, setFilteredYear] = useState('2021');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchExpenses = async () => {
      try {
        const expensesData = await getExpenses(); // Отримати дані про витрати
        setExpenses(expensesData);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching expenses: ', error);
      }
    };

    fetchExpenses();
  }, []); // Порожній масив означає, що ефект викликається лише після першого рендеру

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses = expenses.filter((expense) => {
    return new Date(expense.date).getFullYear().toString() === filteredYear;
  });

  return (
    <div className="App">
      <NewExpense onAddExpense={addExpenseHandler} />
      <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
      <Chart expenses={filteredExpenses} />
      <Expenses items={filteredExpenses} isLoading={isLoading} />
    </div>
  );
};

export default App;
